import Foundation
import Moya
import RxSwift

final class PostsListInteractor {
    var provider: MoyaProvider<PostsManagerPath>
    
    init(provider : MoyaProvider<PostsManagerPath> = .init() ) {
        self.provider = provider
    }
}

// MARK: - Extensions -

extension PostsListInteractor: PostsListInteractorInterface {
    func fetch() -> Observable<[Post]> {
        return provider.rx
            .request(.getPosts)
            .map([Post].self)
            .asObservable()
    }
}
