import UIKit
import RxSwift
import RxCocoa

final class PostsListWireframe: BaseWireframe {

    // MARK: - Module setup -

    init() {
        let moduleViewController = PostsListViewController()
        super.init(viewController: moduleViewController)

        let interactor = PostsListInteractor()
        let presenter = PostsListPresenter(view: moduleViewController, interactor: interactor, wireframe: self)
        moduleViewController.presenter = presenter
    }

}

// MARK: - Extensions -

extension PostsListWireframe: PostsListWireframeInterface {
    func didSelectPost(_ post: Post) {
        viewController.present(UIViewController(), animated: true, completion: nil)
    }
    
}
